// UpsertAllByPage upsert all Message records from the slice.
// This function upserts data by pages to avoid exceeding Postgres limitation (max parameters: 65535)
func (s MessageSlice) UpsertAllByPage(ctx context.Context, exec boil.ContextExecutor, updateOnConflict bool, conflictColumns []string, updateColumns, insertColumns boil.Columns, limits ...int) (int64, error) {
	length := len(s)
	if length == 0 {
		return 0, nil
	}

	// max number of parameters = 65535
	chunkSize := MaxPageSize / reflect.ValueOf(&MessageColumns).Elem().NumField()
	if len(limits) > 0 && limits[0] > 0 && limits[0] < chunkSize {
		chunkSize = limits[0]
	}
	if length <= chunkSize {
		return s.UpsertAll(ctx, exec, updateOnConflict, conflictColumns, updateColumns, insertColumns)
	}

	rowsAffected := int64(0)
	start := 0
	for {
		end := start + chunkSize
		if end > length {
			end = length
		}
		rows, err := s[start:end].UpsertAll(ctx, exec, updateOnConflict, conflictColumns, updateColumns, insertColumns)
		if err != nil {
			return rowsAffected, err
		}

		rowsAffected += rows
		start = end
		if start >= length {
			break
		}
	}
	return rowsAffected, nil
}

// LoadMessageClientsByPage performs eager loading of values by page. This is for a N-1 relationship.
func (s MessageSlice) LoadMessageClientsByPage(ctx context.Context, e boil.ContextExecutor, mods ...qm.QueryMod) error {
	return s.LoadMessageClientsByPageEx(ctx, e, DefaultPageSize, mods...)
}
func (s MessageSlice) LoadMessageClientsByPageEx(ctx context.Context, e boil.ContextExecutor, pageSize int, mods ...qm.QueryMod) error {
	if len(s) == 0 {
		return nil
	}
	for _, chunk := range chunkSlice[*Message](s, pageSize) {
		if err := chunk[0].L.LoadMessageClient(ctx, e, false, &chunk, queryMods(mods)); err != nil {
			return err
		}
	}
	return nil
}

func (s MessageSlice) GetLoadedMessageClients() ClientSlice {
	result := make(ClientSlice, 0, len(s))
	mapCheckDup := make(map[*Client]struct{})
	for _, item := range s {
		if item.R == nil || item.R.MessageClient == nil {
			continue
		}
		if _, ok := mapCheckDup[item.R.MessageClient]; ok {
			continue
		}
		result = append(result, item.R.MessageClient)
		mapCheckDup[item.R.MessageClient] = struct{}{}
	}
	return result
}

